package xna.wrapper;import com.badlogic.gdx.graphics.Texture;public class AssetManager {	private static final String assetPath = "assets";	private static AssetManager instance;	private static final String __spriteSheetName = "GameplaySheet.png";	public static AssetManager Get() {		if (instance == null) {			instance = new AssetManager();		}		return instance;	}	private static String Graphic(String fileName) {		return assetPath + "/graphics/" + fileName;	}	private AssetManager() {	}	public Texture GetImage(String fileName) {		return new Texture(Graphic(fileName));	}}