package xna.wrapper;public enum Keys {	Up(Input.KEY_UP),	Down(Input.KEY_DOWN),	Left(Input.KEY_LEFT),	Right(Input.KEY_RIGHT),	Space(Input.KEY_SPACE),	E(Input.KEY_E),	R(Input.KEY_R),	Enter(Input.KEY_ENTER),	Back(Input.KEY_BACK),	D(Input.KEY_D),	A(Input.KEY_A),	S(Input.KEY_S),	RightControl(Input.KEY_RCONTROL),	D1(Input.KEY_1),	D2(Input.KEY_2),	D3(Input.KEY_3),	OemTilde(Input.KEY_TAB);	private int _keyCode;	private Keys(int keyCode) {		_keyCode = keyCode;	}	public int getKeyCode() {		return _keyCode;	}}