package xna.wrapper;import spx.core.Point2;import com.badlogic.gdx.graphics.Color;import com.badlogic.gdx.graphics.Texture;import com.badlogic.gdx.graphics.g2d.Sprite;public class SpriteBatch {	private com.badlogic.gdx.graphics.g2d.SpriteBatch batch;	private static final Color __defaultFilter = Color.WHITE;	private static final float __defaultDepth = 1f;	private static final float __defaultAlpha = 1f;	public void Draw(Texture image, Point2 location, float depth, Color filter) {		Render(image, location, filter, depth, __defaultAlpha);	}	public void Draw(Texture image, Point2 location, float depth, float alpha) {		Render(image, location, __defaultFilter, depth, alpha);	}	public void Draw(Texture image, Point2 location, float depth) {		Render(image, location, __defaultFilter, depth, __defaultAlpha);	}	public void Draw(Texture image, Point2 location) {		Render(image, location, __defaultFilter, __defaultDepth, __defaultAlpha);	}	private void Render(Texture image, Point2 location, Color filter, float depth, float alpha) {		batch.setColor(filter);		batch.draw(image, location.PosX, location.PosY);		batch.setColor(0, 0, 0, 1);	}	public void DrawString(String content, Point2 location, Color filter, float scale, float depth) {		RenderString(content, location, filter, scale, depth);	}	private void RenderString(String content, Point2 location, Color filter, float scale, float depth) {	}	public void Begin() {		batch.begin();	}	public void End() {		batch.end();	}	public void Draw(Sprite _texture, Point2 _position, float _layerDepth, Color _color) {		_texture.setPosition(_position.PosX, _position.PosY);		_texture.setColor(_color);		_texture.draw(batch);	}	public void Draw(Texture texture, Point2 position, float depth, Color filter, int scaleX, int scaleY) {		batch.draw(texture, position.PosX, position.PosY, 0, 0, scaleX, scaleY);	}}