package xna.wrapper;import spx.core.Point2;import com.badlogic.gdx.graphics.Color;import com.badlogic.gdx.graphics.Texture;public class SpriteBatch {	private static final Color __defaultFilter = Color.WHITE;	private static final float __defaultDepth = 1f;	private static final float __defaultAlpha = 1f;	public void Draw(Texture image, Point2 location, float depth, Color filter) {		Render(image, location, filter, depth, __defaultAlpha);	}	public void Draw(Texture image, Point2 location, float depth, float alpha) {		Render(image, location, __defaultFilter, depth, alpha);	}	public void Draw(Texture image, Point2 location, float depth) {		Render(image, location, __defaultFilter, depth, __defaultAlpha);	}	public void Draw(Texture image, Point2 location) {		Render(image, location, __defaultFilter, __defaultDepth, __defaultAlpha);	}	private void Render(Texture image, Point2 location, Color filter, float depth, float alpha) {		image.draw(location.PosX, location.PosY, filter);	}	public void DrawString(String content, Point2 location, Color filter, float scale, float depth) {		RenderString(content, location, filter, scale, depth);	}	private void RenderString(String content, Point2 location, Color filter, float scale, float depth) {	}}