package com.aigilas.reactions.impl;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.reactions.IReaction;import com.aigilas.statuses.*;import com.aigilas.items.*;import com.spx.entities.*;    public class ExplosionReaction  implements IReaction    {        public void Affect(ICreature target)        {            for (IActor creature:EntityManager.GetActorsSurrounding(target.GetLocation(), 2))            {                ((ICreature)creature).ApplyDamage(10);            }        }    }