package com.aigilas.skills.animations;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.entities.*;import com.spx.core.*;public     class RangedAnimation  extends  SkillAnimation    {@Override        public  void Animate(SkillEffect skill, ICreature source, Point2 velocity)        {            if (!skill.Move(velocity.X, velocity.Y))            {                skill.Cleanup(skill);            }        }    }