package com.aigilas.skills.behaviors;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.entities.*;import com.spx.core.*;import com.spx.entities.*;    public class CloudBehavior extends SkillBehavior    {        public CloudBehavior(int effectGraphic, ISkill parentSkill){        	super(effectGraphic, AnimationType.CLOUD, parentSkill);        }@Overridepublic  void Activate(ICreature target){            if (SubtractCost(target))            {                Point2 referencePoint = target.GetLocation();                for (int ii = -1; ii < 2; ii++)                {                    for (int jj = -1; jj < 2; jj++)                    {                        if (ii != 0 || jj != 0)                        {                            Point2 cloudPosition = new Point2(referencePoint.GridX + ii, referencePoint.GridY + jj);                            _sideEffects.Generate(cloudPosition, new Point2(0, 0), target);                        }                    }                }            }        }    }