package com.aigilas.skills.impl;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.entities.*;import com.aigilas.skills.*;import com.spx.sprites.*;import com.aigilas.statuses.*;import com.aigilas.items.*;import com.spx.entities.*;import com.spx.core.*;    public class WallPunchSkill  extends  ISkill    {        public WallPunchSkill()            { super(SkillId.WALL_PUNCH, AnimationType.RANGED);            Add(Elements.EARTH);            AddCost(StatType.MANA, 10);        }@Override        public  void Affect(IEntity target)        {            if (target.GetEntityType() == com.aigilas.EntityType.WALL)            {                if (target.GetLocation().GridX > 0 && target.GetLocation().GridX < Dungeons.DungeonFactory.BlocksWide - 1 && target.GetLocation().GridY > 0 && target.GetLocation().GridY < Dungeons.DungeonFactory.BlocksHigh - 1)                {                    target.SetInactive();                }            }        }    }