package com.aigilas.status.impl;import com.xna.wrapper.*;import java.util.*;import com.aigilas.creatures.*;import com.aigilas.strategies.*;import com.aigilas.entities.*;import com.aigilas.skills.*;import com.spx.core.*;    public class MutinyStatus  extends  IStatus    {        private int previousStrategy;        private StatBuff buff = new StatBuff(StatType.MOVE_COOL_DOWN, 10);        public MutinyStatus(ICreature target){ super(target);            _prevents.add(CreatureAction.WontHitNonTargets);        }@Override        public  void Setup()        {            super.Setup();            previousStrategy = _target.GetStrategyId();            _target.SetStrategy(StrategyFactory.Create(Strategy.Mutiny, _target));        }@Override        public  void Cleanup()        {            super.Cleanup();            _target.SetStrategy(StrategyFactory.Create(previousStrategy, _target));        }    }